sys_script_include.script:
var toPopulatePrimaryOwner = Class.create();

//*********************************************************************
//This Script Include is used by the catalog items to set the Server and Database owner on the catalog items
//*********************************************************************
toPopulatePrimaryOwner.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	//getOwner function fetches Server CI's Primary Owner approver from the cmdb_ci_computer table
	getOwner : function(){
	var serverId = this.getParameter('server_id');

	var gr = new GlideRecord('cmdb_ci_computer');
	gr.addQuery('sys_id',serverId);
	gr.query();
	//gs.addInfoMessage(serverId);
	if(gr.next()){
		var owner =  gr.owned_by;
		return owner;
	}
	else{
	return ;
	}
},
	//getDatabaseOwner function fetches Database CI's Primary Owner approver from the cmdb_ci_database table
	getDatabaseOwner : function(){
	var databaseId = this.getParameter('database_id');

	var gr = new GlideRecord('cmdb_ci_database');
	gr.addQuery('sys_id',databaseId);
	gr.query();
	//gs.addInfoMessage(serverId);
	if(gr.next()){
		var owner =  gr.owned_by;
		return owner;
	}
	else{
	return ;
	}
},


    type: 'toPopulatePrimaryOwner'
});
